package com.example.mynews.api;import com.example.mynews.models.NewsModel;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface ApiInterface {    @GET("top-headlines")    Call<NewsModel> getNews(@Query("country") String country, @Query("apiKey") String apiKey);    @GET("top-headlines")    Call<NewsModel> getByCategory(@Query("country") String country,@Query("category") String category, @Query("apiKey") String apiKey);    @GET("everything")    Call<NewsModel> getNewsSearch(@Query("q") String keyword, @Query("language") String language            , @Query("sortBy") String sortBy,                                  @Query("apiKey") String api);}